CREATE TABLE `cs_exams_tmp` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `question` text NOT NULL,
  `an1` varchar(100) DEFAULT NULL COMMENT '答案一',
  `an2` varchar(100) DEFAULT NULL COMMENT '答案二',
  `an3` varchar(100) DEFAULT NULL COMMENT '答案三',
  `an4` varchar(100) DEFAULT NULL COMMENT '答案四',
  `answertrue` int(2) DEFAULT NULL COMMENT '正确答案',
  `imageurl` varchar(200) DEFAULT NULL COMMENT '题目图片',
  `explain` text COMMENT '答案解析',
  `type` int(11) NOT NULL DEFAULT '0' COMMENT '题目类型：1.判断题 2.单选题 3.多选题',
  `chapterid` int(11) NOT NULL DEFAULT '0' COMMENT '章节ID',
  `SpeId` varchar(100) DEFAULT NULL COMMENT '特殊id',
  `ctype` varchar(10) DEFAULT NULL COMMENT '牌照类型 C1 C2',
  `stype` int(2) NOT NULL DEFAULT '0' COMMENT '科目 ',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7561 DEFAULT CHARSET=utf8 COMMENT='科目考试题目';
